jQuery(document).ready(function(){SOAPERY_STORAGE.media_frame=null;SOAPERY_STORAGE.media_link="";jQuery(".soapery_media_selector").on("click",function(a){soapery_show_media_manager(this);a.preventDefault();return false})});function soapery_show_media_manager(a){SOAPERY_STORAGE.media_link=jQuery(a);if(SOAPERY_STORAGE.media_frame){SOAPERY_STORAGE.media_frame.open();return false}SOAPERY_STORAGE.media_frame=wp.media({title:SOAPERY_STORAGE.media_link.data("choose"),library:{type:"image"},multiple:SOAPERY_STORAGE.media_link.data("multiple")===true?"add":false,button:{text:SOAPERY_STORAGE.media_link.data("update"),close:true}});SOAPERY_STORAGE.media_frame.on("select",function(b){var c=jQuery("#"+SOAPERY_STORAGE.media_link.data("linked-field")).eq(0);var e="";if(SOAPERY_STORAGE.media_link.data("multiple")===true){SOAPERY_STORAGE.media_frame.state().get("selection").map(function(f){e+=(e?"\n":"")+f.toJSON().url});var d=c.val();e=d+(d?"\n":"")+e}else{e=SOAPERY_STORAGE.media_frame.state().get("selection").first().toJSON().url}c.val(e);if(c.siblings("img").length>0){c.siblings("img").attr("src",e)}c.trigger("change")});SOAPERY_STORAGE.media_frame.open();return false};